#!/bin/bash
find . -type f -name '*.ll' | xargs -I '{}' cp '{}' '{}.golden'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "; <label>:([0-9]+)[^\n]+\n" "\$1:\n" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i ";[^\n]+\n" "" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "attributes[^\n]+\n" "" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i " #[0-9]+" "" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "[!][^\n]+\n" "" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i ", align [0-9]+\n" "\n" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i " nsw " " " '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i " inbounds " " " '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "^\n" "" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "\n\n" "\n" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "\n\n" "\n" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "  " "\t" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i "define[^\n]*" "\$0\n0:" '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i " common " " " '{}'
find . -type f -name '*.ll.golden' | xargs -I '{}' sar -i " signext " " " '{}'
